import React, { useState, useEffect } from "react";
const salePriceData = require('./data/median-sale-price.json')


const App = () => {
    const [income, setIncome] = useState();
    const [tractData, setTractData] = useState(salePriceData);
    const [affordablePayment, setAffordablePayment] = useState();
    const [affordableLoan, setAffordableLoan] = useState();
    const padding = 10;

    console.log(income);

    const affordableSalePrice = (income, year, apr) => {
        const affordablePayment = (income * .3)/12;
        const n = year * 12;
        const c = apr/12;
        const paymentDivisor = (c * Math.pow((c + 1),n))/(Math.pow((c + 1) - 1));
        const affordableLoan = parseFloat(affordablePayment) / parseFloat(paymentDivisor);
        setAffordablePayment(affordablePayment);
        setAffordableLoan(affordableLoan);
    }

    return(
        <div style={{
            padding: padding
        }}>
            <h2>Housing Affordability Calculator</h2>
            <input 
                name='income'
                placeholder='Enter Income'
                // value={income}
                onChange={event => setIncome(event.target.value)}
            />
            <button
                onClick={() => affordableSalePrice(income, 3, .045)} 
                style={{
                    margin: 10
                }}
            >
                Submit
            </button>
            <h3>Affordable Monthly Payment: ${affordablePayment}</h3>
            <h3>Affordable Loan: ${affordableLoan}</h3>
        </div>
    )
};

export default App;
