import React, { useState, useEffect } from "react";
const salePriceData = require('./data/median-sale-price.json')


const App = () => {
    const [income, setIncome] = useState();
    const [tractData, setTractData] = useState(salePriceData);
    const [affordablePayment, setAffordablePayment] = useState();
    const [salePrice, setSalePrice] = useState();
    const [affordableLoan, setAffordableLoan] = useState();
    const padding = 10;

    console.log(income);

    const runCalc = (income, salePrice, year, apr) => {
        const percentOfIncome = .2;
        const affordablePayment = (income * percentOfIncome)/12;
        const n = year * 12;
        const c = apr/12;
        const mpr = Math.pow((c + 1),n);
        const paymentDivisor = (c * Math.pow((c + 1),n))/(Math.pow((c + 1), n) - 1);
        const affordableLoan = parseFloat(affordablePayment) / parseFloat(paymentDivisor);    

        console.log(affordablePayment);
        console.log(mpr);
        console.log(paymentDivisor);
        console.log(affordableLoan);

        setAffordablePayment(affordablePayment);
        // setAffordableLoan(affordableLoan);
    }

    return(
        <div style={{
            padding: padding
        }}>
            <h2>Housing Affordability Calculator</h2>
            <input 
                name='income'
                placeholder='Enter Income'
                // value={income}
                onChange={event => setIncome(event.target.value)}
            />
            <input 
                name='home-sale-price'
                placeholder='Enter Sale Price'
                // value={income}
                onChange={event => setSalePrice(event.target.value)}
            />
            <button
                onClick={() => runCalc(income, salePrice, 30, .045)} 
                style={{
                    margin: 10
                }}
            >
                Submit
            </button>
            <h3>Affordable Monthly Payment: ${affordablePayment}</h3>
            {/* <h3>Affordable Loan: ${affordableLoan}</h3> */}
        </div>
    )
};

export default App;
