import React, { useState, useEffect } from "react";
const salePriceData = require('./data/median-sale-price.json')


const App = () => {
    const [income, setIncome] = useState();
    const [salePrices, setSalePrices] = useState(salePriceData);
    const [affordablePayment, setAffordablePayment] = useState();
    // const [affordableSalePrice, setAffordableSalePrice] = useState();
    const padding = 10;

    console.log(income);

    const affordableSalePrice = (income) => {
        const affordablePayment = (income * .3)/12;
        setAffordablePayment(affordablePayment);
    }

    return(
        <div style={{
            padding: padding
        }}>
            <h2>Housing Affordability Calculator</h2>
            <input 
                name='income'
                placeholder='Enter Income'
                // value={income}
                onChange={event => setIncome(event.target.value)}
            />
            <button
                onClick={() => affordableSalePrice(income)} 
                style={{
                    margin: 10
                }}
            >
                Submit
            </button>
            <h3>Affordable Monthly Payment: ${affordablePayment}</h3>
        </div>
    )
};

export default App;
